let inp=document.getElementById("numberInput"),numberWrapper=document.querySelector(".number"),numbers=document.querySelectorAll("span"),callBtn=document.querySelector(".call"),deleteBtn=document.querySelector(".delete");numbers.forEach(e=>{let t=e.getAttribute("data-number");e.addEventListener("click",(function(){(inp.value+t).length<11&&(inp.value+=t)}))}),callBtn.addEventListener("click",(function(e){callBtn.setAttribute("href","tel:+"+inp.value)})),deleteBtn.addEventListener("click",(function(){inp.value=inp.value.slice(0,inp.value.length-1)}));